<?phpdeclare(strict_types=1);use GDO\Shoutbox\GDO_Shoutbox;use GDO\UI\GDT_Button;use GDO\User\GDO_User;/** @var GDO_Shoutbox $shout * */?><div class="gdo-shoutbox-message">    <div>        <b><?=$shout->getCreator()->renderName()?></b>        <span><?=$shout->displayCreated()?></span>    </div>    <p><?=$shout->gdoDisplay('shout_text')?></p>	<?php	if ($shout->canDelete(GDO_User::current())) : ?>		<?=GDT_Button::make()->href($shout->hrefEdit())->icon('edit')->render()?>	<?php	endif; ?></div>